import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Image, Spinner } from 'react-bootstrap';
import { facultyAPI, aboutAPI } from '../services/api';

const About = () => {
  const [faculty, setFaculty] = useState([]);
  const [aboutContent, setAboutContent] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [facultyResponse, aboutResponse] = await Promise.all([
        facultyAPI.getFaculty(),
        aboutAPI.getContent()
      ]);
      
      setFaculty(facultyResponse.data.data);
      setAboutContent(aboutResponse.data.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-spinner">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </div>
    );
  }

  return (
    <Container className="py-5">
      {/* About Content */}
      <Row className="mb-5">
        <Col>
          <h1 className="text-center school-primary mb-5">About Genesis School</h1>
          
          {/* Mission */}
          {aboutContent.mission && (
            <Card className="mb-4 border-0 shadow-sm">
              <Card.Body className="p-4">
                <h3 className="school-primary mb-3">
                  {aboutContent.mission.title || 'Our Mission'}
                </h3>
                <p className="lead text-muted">
                  {aboutContent.mission.content}
                </p>
                {aboutContent.mission.image_url && (
                  <div className="text-center mt-3">
                    <Image 
                      src={`${process.env.REACT_APP_API_URL}${aboutContent.mission.image_url}`}
                      alt="Mission"
                      fluid
                      className="rounded"
                      style={{ maxHeight: '300px' }}
                    />
                  </div>
                )}
              </Card.Body>
            </Card>
          )}

          {/* Vision */}
          {aboutContent.vision && (
            <Card className="mb-4 border-0 shadow-sm">
              <Card.Body className="p-4">
                <h3 className="school-primary mb-3">
                  {aboutContent.vision.title || 'Our Vision'}
                </h3>
                <p className="lead text-muted">
                  {aboutContent.vision.content}
                </p>
                {aboutContent.vision.image_url && (
                  <div className="text-center mt-3">
                    <Image 
                      src={`${process.env.REACT_APP_API_URL}${aboutContent.vision.image_url}`}
                      alt="Vision"
                      fluid
                      className="rounded"
                      style={{ maxHeight: '300px' }}
                    />
                  </div>
                )}
              </Card.Body>
            </Card>
          )}

          {/* History */}
          {aboutContent.history && (
            <Card className="mb-4 border-0 shadow-sm">
              <Card.Body className="p-4">
                <h3 className="school-primary mb-3">
                  {aboutContent.history.title || 'Our History'}
                </h3>
                <p className="text-muted">
                  {aboutContent.history.content}
                </p>
                {aboutContent.history.image_url && (
                  <div className="text-center mt-3">
                    <Image 
                      src={`${process.env.REACT_APP_API_URL}${aboutContent.history.image_url}`}
                      alt="History"
                      fluid
                      className="rounded"
                      style={{ maxHeight: '300px' }}
                    />
                  </div>
                )}
              </Card.Body>
            </Card>
          )}

          {/* Facilities */}
          {aboutContent.facilities && (
            <Card className="mb-4 border-0 shadow-sm">
              <Card.Body className="p-4">
                <h3 className="school-primary mb-3">
                  {aboutContent.facilities.title || 'Our Facilities'}
                </h3>
                <p className="text-muted">
                  {aboutContent.facilities.content}
                </p>
                {aboutContent.facilities.image_url && (
                  <div className="text-center mt-3">
                    <Image 
                      src={`${process.env.REACT_APP_API_URL}${aboutContent.facilities.image_url}`}
                      alt="Facilities"
                      fluid
                      className="rounded"
                      style={{ maxHeight: '300px' }}
                    />
                  </div>
                )}
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>

      {/* Faculty Section */}
      <Row>
        <Col>
          <h2 className="text-center school-primary mb-5">Our Faculty</h2>
          
          {faculty.length > 0 ? (
            <Row className="g-4">
              {faculty.map((member) => (
                <Col md={6} lg={4} key={member.id}>
                  <Card className="h-100 faculty-card border-0 shadow-sm">
                    {member.image_url && (
                      <div className="text-center p-3">
                        <Image
                          src={`${process.env.REACT_APP_API_URL}${member.image_url}`}
                          alt={member.name}
                          roundedCircle
                          style={{ width: '150px', height: '150px', objectFit: 'cover' }}
                        />
                      </div>
                    )}
                    <Card.Body className="text-center">
                      <Card.Title className="school-primary">{member.name}</Card.Title>
                      <Card.Subtitle className="mb-2 text-muted">
                        {member.position}
                      </Card.Subtitle>
                      {member.department && (
                        <p className="text-muted small mb-2">
                          {member.department}
                        </p>
                      )}
                      {member.qualification && (
                        <p className="text-muted small mb-2">
                          <strong>Qualification:</strong> {member.qualification}
                        </p>
                      )}
                      {member.experience && (
                        <p className="text-muted small mb-2">
                          <strong>Experience:</strong> {member.experience}
                        </p>
                      )}
                      {member.bio && (
                        <Card.Text className="text-muted small">
                          {member.bio}
                        </Card.Text>
                      )}
                      {member.email && (
                        <p className="text-muted small">
                          <strong>Email:</strong> {member.email}
                        </p>
                      )}
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </Row>
          ) : (
            <div className="text-center text-muted">
              <p>Faculty information will be available soon.</p>
            </div>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default About;
