import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Tab, Nav, Card, Image, Spinner, Modal } from 'react-bootstrap';
import { galleryAPI } from '../services/api';

const Gallery = () => {
  const [albums, setAlbums] = useState([]);
  const [videos, setVideos] = useState([]);
  const [selectedAlbum, setSelectedAlbum] = useState(null);
  const [albumImages, setAlbumImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [showImageModal, setShowImageModal] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [albumsResponse, videosResponse] = await Promise.all([
        galleryAPI.getAlbums(),
        galleryAPI.getVideos()
      ]);
      
      setAlbums(albumsResponse.data.data);
      setVideos(videosResponse.data.data);
    } catch (error) {
      console.error('Error fetching gallery data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchAlbumImages = async (albumId) => {
    try {
      const response = await galleryAPI.getImagesByAlbum(albumId);
      setAlbumImages(response.data.data);
    } catch (error) {
      console.error('Error fetching album images:', error);
    }
  };

  const handleAlbumSelect = (album) => {
    setSelectedAlbum(album);
    fetchAlbumImages(album.id);
  };

  const handleImageClick = (image) => {
    setSelectedImage(image);
    setShowImageModal(true);
  };

  if (loading) {
    return (
      <div className="loading-spinner">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </div>
    );
  }

  return (
    <Container className="py-5">
      <h1 className="text-center school-primary mb-5">Gallery</h1>
      
      <Tab.Container defaultActiveKey="photos">
        <Row>
          <Col>
            <Nav variant="tabs" className="justify-content-center mb-4">
              <Nav.Item>
                <Nav.Link eventKey="photos">Photos</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="albums">Albums</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="videos">Videos</Nav.Link>
              </Nav.Item>
            </Nav>
          </Col>
        </Row>

        <Tab.Content>
          {/* Photos Tab */}
          <Tab.Pane eventKey="photos">
            <Row>
              <Col md={3}>
                <h4 className="school-primary mb-3">Photo Albums</h4>
                {albums.length > 0 ? (
                  <div className="list-group">
                    {albums.map((album) => (
                      <button
                        key={album.id}
                        className={`list-group-item list-group-item-action ${
                          selectedAlbum?.id === album.id ? 'active' : ''
                        }`}
                        onClick={() => handleAlbumSelect(album)}
                      >
                        <div className="d-flex align-items-center">
                          {album.cover_image && (
                            <Image
                              src={`${process.env.REACT_APP_API_URL}${album.cover_image}`}
                              alt={album.name}
                              style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                              className="rounded me-3"
                            />
                          )}
                          <div>
                            <h6 className="mb-1">{album.name}</h6>
                            {album.description && (
                              <small className="text-muted">{album.description}</small>
                            )}
                          </div>
                        </div>
                      </button>
                    ))}
                  </div>
                ) : (
                  <p className="text-muted">No albums available.</p>
                )}
              </Col>
              
              <Col md={9}>
                {selectedAlbum ? (
                  <div>
                    <h4 className="school-primary mb-3">{selectedAlbum.name}</h4>
                    {selectedAlbum.description && (
                      <p className="text-muted mb-4">{selectedAlbum.description}</p>
                    )}
                    
                    {albumImages.length > 0 ? (
                      <Row className="g-3">
                        {albumImages.map((image) => (
                          <Col md={4} key={image.id}>
                            <div 
                              className="gallery-item cursor-pointer"
                              onClick={() => handleImageClick(image)}
                            >
                              <Image
                                src={`${process.env.REACT_APP_API_URL}${image.image_url}`}
                                alt={image.title || 'Gallery image'}
                                fluid
                                className="rounded shadow-sm"
                                style={{ height: '200px', objectFit: 'cover', width: '100%' }}
                              />
                              {image.title && (
                                <div className="mt-2">
                                  <h6 className="mb-1">{image.title}</h6>
                                  {image.description && (
                                    <small className="text-muted">{image.description}</small>
                                  )}
                                </div>
                              )}
                            </div>
                          </Col>
                        ))}
                      </Row>
                    ) : (
                      <p className="text-muted">No images in this album.</p>
                    )}
                  </div>
                ) : (
                  <div className="text-center text-muted">
                    <p>Select an album to view photos.</p>
                  </div>
                )}
              </Col>
            </Row>
          </Tab.Pane>

          {/* Albums Tab */}
          <Tab.Pane eventKey="albums">
            <Row className="g-4">
              {albums.length > 0 ? (
                albums.map((album) => (
                  <Col md={6} lg={4} key={album.id}>
                    <Card className="h-100 border-0 shadow-sm">
                      {album.cover_image && (
                        <Image
                          src={`${process.env.REACT_APP_API_URL}${album.cover_image}`}
                          alt={album.name}
                          style={{ height: '200px', objectFit: 'cover' }}
                        />
                      )}
                      <Card.Body>
                        <Card.Title className="school-primary">{album.name}</Card.Title>
                        {album.description && (
                          <Card.Text className="text-muted">
                            {album.description}
                          </Card.Text>
                        )}
                      </Card.Body>
                    </Card>
                  </Col>
                ))
              ) : (
                <Col>
                  <div className="text-center text-muted">
                    <p>No albums available.</p>
                  </div>
                </Col>
              )}
            </Row>
          </Tab.Pane>

          {/* Videos Tab */}
          <Tab.Pane eventKey="videos">
            <Row className="g-4">
              {videos.length > 0 ? (
                videos.map((video) => (
                  <Col md={6} lg={4} key={video.id}>
                    <Card className="h-100 border-0 shadow-sm">
                      {video.thumbnail_url ? (
                        <Image
                          src={`${process.env.REACT_APP_API_URL}${video.thumbnail_url}`}
                          alt={video.title}
                          style={{ height: '200px', objectFit: 'cover' }}
                        />
                      ) : (
                        <div 
                          className="bg-secondary d-flex align-items-center justify-content-center"
                          style={{ height: '200px' }}
                        >
                          <span className="text-white">ðŸ“¹</span>
                        </div>
                      )}
                      <Card.Body>
                        <Card.Title className="school-primary">{video.title}</Card.Title>
                        {video.description && (
                          <Card.Text className="text-muted">
                            {video.description}
                          </Card.Text>
                        )}
                        <a 
                          href={video.video_url}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="btn btn-primary btn-sm"
                        >
                          Watch Video
                        </a>
                      </Card.Body>
                    </Card>
                  </Col>
                ))
              ) : (
                <Col>
                  <div className="text-center text-muted">
                    <p>No videos available.</p>
                  </div>
                </Col>
              )}
            </Row>
          </Tab.Pane>
        </Tab.Content>
      </Tab.Container>

      {/* Image Modal */}
      <Modal show={showImageModal} onHide={() => setShowImageModal(false)} size="lg" centered>
        <Modal.Header closeButton>
          <Modal.Title>{selectedImage?.title || 'Gallery Image'}</Modal.Title>
        </Modal.Header>
        <Modal.Body className="text-center">
          {selectedImage && (
            <Image
              src={`${process.env.REACT_APP_API_URL}${selectedImage.image_url}`}
              alt={selectedImage.title || 'Gallery image'}
              fluid
            />
          )}
          {selectedImage?.description && (
            <p className="mt-3 text-muted">{selectedImage.description}</p>
          )}
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default Gallery;
