import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Tab, Nav, Card, Image, Spinner, Modal } from 'react-bootstrap';
import { galleryAPI } from '../services/api';

const Gallery = () => {
  const [selectedAlbum, setSelectedAlbum] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [showImageModal, setShowImageModal] = useState(false);

  // Hardcoded gallery data with school images
  const albums = [
    {
      id: 1,
      name: "School Building & Infrastructure",
      description: "Beautiful campus and modern facilities",
      cover_image: "/images/school-building.jpg",
      images: [
        { id: 1, title: "Main School Building", description: "Our beautiful main building", image_url: "/images/school-building.jpg" },
        { id: 2, title: "School Entrance", description: "Welcoming entrance to Srijan School", image_url: "/images/school-entrance.jpg" },
        { id: 3, title: "Playground", description: "Spacious playground for sports", image_url: "/images/playground.jpg" },
        { id: 4, title: "Library", description: "Well-stocked library with modern facilities", image_url: "/images/library.jpg" },
        { id: 5, title: "Science Laboratory", description: "State-of-the-art science lab", image_url: "/images/science-lab.jpg" },
        { id: 6, title: "Computer Lab", description: "Modern computer laboratory", image_url: "/images/computer-lab.jpg" }
      ]
    },
    {
      id: 2,
      name: "Students & Activities",
      description: "Students engaged in various activities",
      cover_image: "/images/students-classroom.jpg",
      images: [
        { id: 7, title: "Students in Classroom", description: "Active learning environment", image_url: "/images/students-classroom.jpg" },
        { id: 8, title: "Group Study", description: "Students working together", image_url: "/images/group-study.jpg" },
        { id: 9, title: "Art & Craft", description: "Creative activities", image_url: "/images/art-craft.jpg" },
        { id: 10, title: "Music Class", description: "Students learning music", image_url: "/images/music-class.jpg" },
        { id: 11, title: "Dance Performance", description: "Cultural dance performance", image_url: "/images/dance-performance.jpg" },
        { id: 12, title: "Sports Activities", description: "Students playing sports", image_url: "/images/sports-activities.jpg" }
      ]
    },
    {
      id: 3,
      name: "Events & Celebrations",
      description: "School events and special occasions",
      cover_image: "/images/independence-day.jpg",
      images: [
        { id: 13, title: "Independence Day", description: "Patriotic celebrations", image_url: "/images/independence-day.jpg" },
        { id: 14, title: "Annual Day", description: "Annual day celebrations", image_url: "/images/annual-day.jpg" },
        { id: 15, title: "Science Exhibition", description: "Students showcasing science projects", image_url: "/images/science-exhibition.jpg" },
        { id: 16, title: "Sports Day", description: "Annual sports meet", image_url: "/images/sports-day.jpg" },
        { id: 17, title: "Cultural Fest", description: "Cultural festival celebrations", image_url: "/images/cultural-fest.jpg" },
        { id: 18, title: "Graduation Ceremony", description: "Graduation day ceremony", image_url: "/images/graduation-ceremony.jpg" }
      ]
    },
    {
      id: 4,
      name: "Teachers & Staff",
      description: "Our dedicated teaching staff",
      cover_image: "/images/teachers-staff.jpg",
      images: [
        { id: 19, title: "Teaching Staff", description: "Our dedicated teachers", image_url: "/images/teachers-staff.jpg" },
        { id: 20, title: "Principal & Management", description: "School leadership team", image_url: "/images/principal-management.jpg" },
        { id: 21, title: "Support Staff", description: "Supporting our students", image_url: "/images/support-staff.jpg" },
        { id: 22, title: "Teacher Training", description: "Continuous professional development", image_url: "/images/teacher-training.jpg" }
      ]
    }
  ];

  const videos = [
    {
      id: 1,
      title: "School Tour Video",
      description: "Virtual tour of our beautiful campus",
      video_url: "https://www.youtube.com/watch?v=school-tour",
      thumbnail_url: "/images/school-tour-thumb.jpg"
    },
    {
      id: 2,
      title: "Annual Day Performance",
      description: "Highlights from our annual day celebrations",
      video_url: "https://www.youtube.com/watch?v=annual-day",
      thumbnail_url: "/images/annual-day-thumb.jpg"
    },
    {
      id: 3,
      title: "Sports Day Highlights",
      description: "Exciting moments from sports day",
      video_url: "https://www.youtube.com/watch?v=sports-day",
      thumbnail_url: "/images/sports-day-thumb.jpg"
    }
  ];

  const handleAlbumSelect = (album) => {
    setSelectedAlbum(album);
  };

  const handleImageClick = (image) => {
    setSelectedImage(image);
    setShowImageModal(true);
  };

  // Remove loading state since we're using hardcoded data

  return (
    <Container className="py-5">
      <h1 className="text-center text-gradient-primary mb-5">Gallery</h1>
      
      <Tab.Container defaultActiveKey="photos">
        <Row>
          <Col>
            <Nav variant="tabs" className="justify-content-center mb-4">
              <Nav.Item>
                <Nav.Link eventKey="photos">Photos</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="albums">Albums</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="videos">Videos</Nav.Link>
              </Nav.Item>
            </Nav>
          </Col>
        </Row>

        <Tab.Content>
          {/* Photos Tab */}
          <Tab.Pane eventKey="photos">
            <Row>
              <Col md={3}>
                <h4 className="school-primary mb-3">Photo Albums</h4>
                {albums.length > 0 ? (
                  <div className="list-group">
                    {albums.map((album) => (
                      <button
                        key={album.id}
                        className={`list-group-item list-group-item-action ${
                          selectedAlbum?.id === album.id ? 'active' : ''
                        }`}
                        onClick={() => handleAlbumSelect(album)}
                      >
                        <div className="d-flex align-items-center">
                          {album.cover_image && (
                            <Image
                              src={`${process.env.REACT_APP_API_URL}${album.cover_image}`}
                              alt={album.name}
                              style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                              className="rounded me-3"
                            />
                          )}
                          <div>
                            <h6 className="mb-1">{album.name}</h6>
                            {album.description && (
                              <small className="text-muted">{album.description}</small>
                            )}
                          </div>
                        </div>
                      </button>
                    ))}
                  </div>
                ) : (
                  <p className="text-muted">No albums available.</p>
                )}
              </Col>
              
              <Col md={9}>
                {selectedAlbum ? (
                  <div>
                    <h4 className="text-gradient-primary mb-3">{selectedAlbum.name}</h4>
                    {selectedAlbum.description && (
                      <p className="text-muted mb-4">{selectedAlbum.description}</p>
                    )}
                    
                    {selectedAlbum.images.length > 0 ? (
                      <Row className="g-3">
                        {selectedAlbum.images.map((image) => (
                          <Col md={4} key={image.id}>
                            <div 
                              className="gallery-item cursor-pointer"
                              onClick={() => handleImageClick(image)}
                              style={{ cursor: 'pointer' }}
                            >
                              <Image
                                src={image.image_url}
                                alt={image.title || 'Gallery image'}
                                fluid
                                className="rounded shadow-sm"
                                style={{ height: '200px', objectFit: 'cover', width: '100%' }}
                                onError={(e) => {
                                  e.target.src = 'https://via.placeholder.com/400x200/dc2626/ffffff?text=School+Image';
                                }}
                              />
                              {image.title && (
                                <div className="mt-2">
                                  <h6 className="mb-1">{image.title}</h6>
                                  {image.description && (
                                    <small className="text-muted">{image.description}</small>
                                  )}
                                </div>
                              )}
                            </div>
                          </Col>
                        ))}
                      </Row>
                    ) : (
                      <p className="text-muted">No images in this album.</p>
                    )}
                  </div>
                ) : (
                  <div className="text-center text-muted">
                    <p>Select an album to view photos.</p>
                  </div>
                )}
              </Col>
            </Row>
          </Tab.Pane>

          {/* Albums Tab */}
          <Tab.Pane eventKey="albums">
            <Row className="g-4">
              {albums.length > 0 ? (
                albums.map((album) => (
                  <Col md={6} lg={4} key={album.id}>
                    <Card className="h-100 card-elevated">
                      {album.cover_image && (
                        <Image
                          src={album.cover_image}
                          alt={album.name}
                          style={{ height: '200px', objectFit: 'cover' }}
                          onError={(e) => {
                            e.target.src = 'https://via.placeholder.com/400x200/dc2626/ffffff?text=School+Album';
                          }}
                        />
                      )}
                      <Card.Body>
                        <Card.Title className="text-gradient-primary">{album.name}</Card.Title>
                        {album.description && (
                          <Card.Text className="text-muted">
                            {album.description}
                          </Card.Text>
                        )}
                      </Card.Body>
                    </Card>
                  </Col>
                ))
              ) : (
                <Col>
                  <div className="text-center text-muted">
                    <p>No albums available.</p>
                  </div>
                </Col>
              )}
            </Row>
          </Tab.Pane>

          {/* Videos Tab */}
          <Tab.Pane eventKey="videos">
            <Row className="g-4">
              {videos.length > 0 ? (
                videos.map((video) => (
                  <Col md={6} lg={4} key={video.id}>
                    <Card className="h-100 card-elevated">
                      {video.thumbnail_url ? (
                        <Image
                          src={video.thumbnail_url}
                          alt={video.title}
                          style={{ height: '200px', objectFit: 'cover' }}
                          onError={(e) => {
                            e.target.src = 'https://via.placeholder.com/400x200/dc2626/ffffff?text=Video+Thumbnail';
                          }}
                        />
                      ) : (
                        <div 
                          className="bg-secondary d-flex align-items-center justify-content-center"
                          style={{ height: '200px' }}
                        >
                          <span className="text-white">ðŸ“¹</span>
                        </div>
                      )}
                      <Card.Body>
                        <Card.Title className="text-gradient-primary">{video.title}</Card.Title>
                        {video.description && (
                          <Card.Text className="text-muted">
                            {video.description}
                          </Card.Text>
                        )}
                        <a 
                          href={video.video_url}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="btn-school-primary btn-sm"
                        >
                          Watch Video
                        </a>
                      </Card.Body>
                    </Card>
                  </Col>
                ))
              ) : (
                <Col>
                  <div className="text-center text-muted">
                    <p>No videos available.</p>
                  </div>
                </Col>
              )}
            </Row>
          </Tab.Pane>
        </Tab.Content>
      </Tab.Container>

      {/* Image Modal */}
      <Modal show={showImageModal} onHide={() => setShowImageModal(false)} size="lg" centered>
        <Modal.Header closeButton>
          <Modal.Title>{selectedImage?.title || 'Gallery Image'}</Modal.Title>
        </Modal.Header>
        <Modal.Body className="text-center">
          {selectedImage && (
            <Image
              src={`${process.env.REACT_APP_API_URL}${selectedImage.image_url}`}
              alt={selectedImage.title || 'Gallery image'}
              fluid
            />
          )}
          {selectedImage?.description && (
            <p className="mt-3 text-muted">{selectedImage.description}</p>
          )}
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default Gallery;
