import React, { useState, useEffect } from 'react';
import { Container, Carousel, Row, Col, Card, Button } from 'react-bootstrap';
import { carouselAPI } from '../services/api';

const Home = () => {
  const [carouselSlides, setCarouselSlides] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCarouselSlides();
  }, []);

  const fetchCarouselSlides = async () => {
    try {
      const response = await carouselAPI.getSlides();
      setCarouselSlides(response.data.data);
    } catch (error) {
      console.error('Error fetching carousel slides:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div>
      {/* Hero Carousel */}
      <Carousel fade className="mb-5">
        {carouselSlides.length > 0 ? (
          carouselSlides.map((slide) => (
            <Carousel.Item key={slide.id}>
              <div
                className="d-block w-100"
                style={{
                  height: '500px',
                  backgroundImage: `url(${process.env.REACT_APP_API_URL}${slide.image_url})`,
                  backgroundSize: 'cover',
                  backgroundPosition: 'center',
                }}
              />
              <Carousel.Caption className="bg-dark bg-opacity-50 rounded p-3">
                <h3 className="school-secondary">{slide.title}</h3>
                {slide.description && (
                  <p className="lead">{slide.description}</p>
                )}
                {slide.link_url && (
                  <Button 
                    variant="primary" 
                    href={slide.link_url}
                    className="btn-school-primary"
                  >
                    Learn More
                  </Button>
                )}
              </Carousel.Caption>
            </Carousel.Item>
          ))
        ) : (
          <Carousel.Item>
            <div
              className="d-block w-100 bg-primary"
              style={{ height: '500px' }}
            />
            <Carousel.Caption className="bg-dark bg-opacity-50 rounded p-3">
              <h3 className="school-secondary">Welcome to Genesis School</h3>
              <p className="lead">Excellence in Education Since 1995</p>
              <Button 
                variant="primary" 
                href="/about"
                className="btn-school-primary"
              >
                Learn More
              </Button>
            </Carousel.Caption>
          </Carousel.Item>
        )}
      </Carousel>

      {/* Welcome Section */}
      <Container className="py-5">
        <Row className="text-center mb-5">
          <Col>
            <h2 className="school-primary mb-3">Welcome to Genesis School</h2>
            <p className="lead text-muted">
              Nurturing young minds for a brighter tomorrow through quality education and holistic development.
            </p>
          </Col>
        </Row>

        {/* Features */}
        <Row className="g-4">
          <Col md={4}>
            <Card className="h-100 text-center border-0 shadow-sm">
              <Card.Body className="p-4">
                <div className="school-primary mb-3" style={{ fontSize: '3rem' }}>
                  🎓
                </div>
                <Card.Title className="school-primary">Quality Education</Card.Title>
                <Card.Text className="text-muted">
                  We provide world-class education with experienced faculty and modern teaching methods.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
          
          <Col md={4}>
            <Card className="h-100 text-center border-0 shadow-sm">
              <Card.Body className="p-4">
                <div className="school-primary mb-3" style={{ fontSize: '3rem' }}>
                  🏆
                </div>
                <Card.Title className="school-primary">Excellence</Card.Title>
                <Card.Text className="text-muted">
                  Our students consistently achieve outstanding results in academics and extracurricular activities.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
          
          <Col md={4}>
            <Card className="h-100 text-center border-0 shadow-sm">
              <Card.Body className="p-4">
                <div className="school-primary mb-3" style={{ fontSize: '3rem' }}>
                  🌟
                </div>
                <Card.Title className="school-primary">Holistic Development</Card.Title>
                <Card.Text className="text-muted">
                  We focus on developing well-rounded individuals with strong character and values.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* Call to Action */}
        <Row className="mt-5">
          <Col className="text-center">
            <h3 className="school-primary mb-3">Ready to Join Us?</h3>
            <p className="text-muted mb-4">
              Discover more about our programs, facilities, and admission process.
            </p>
            <div className="d-flex justify-content-center gap-3 flex-wrap">
              <Button 
                variant="primary" 
                size="lg"
                href="/about"
                className="btn-school-primary"
              >
                Learn More
              </Button>
              <Button 
                variant="outline-primary" 
                size="lg"
                href="/contact"
              >
                Contact Us
              </Button>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Home;
