import React, { useState } from 'react';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
  const [expanded, setExpanded] = useState(false);
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <Navbar 
      bg="light" 
      expand="lg" 
      className="shadow-sm"
      expanded={expanded}
    >
      <Container>
        <Navbar.Brand as={Link} to="/" className="fw-bold school-primary">
          ðŸŽ“ Genesis School
        </Navbar.Brand>
        
        <Navbar.Toggle 
          aria-controls="basic-navbar-nav" 
          onClick={() => setExpanded(!expanded)}
        />
        
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <Nav.Link 
              as={Link} 
              to="/" 
              className={isActive('/') ? 'school-primary fw-bold' : ''}
              onClick={() => setExpanded(false)}
            >
              Home
            </Nav.Link>
            
            <Nav.Link 
              as={Link} 
              to="/about" 
              className={isActive('/about') ? 'school-primary fw-bold' : ''}
              onClick={() => setExpanded(false)}
            >
              About Us
            </Nav.Link>
            
            <NavDropdown title="Academics" id="academics-dropdown">
              <NavDropdown.Item as={Link} to="/rules" onClick={() => setExpanded(false)}>
                School Rules
              </NavDropdown.Item>
              <NavDropdown.Item as={Link} to="/fees" onClick={() => setExpanded(false)}>
                Fee Structure
              </NavDropdown.Item>
            </NavDropdown>
            
            <Nav.Link 
              as={Link} 
              to="/gallery" 
              className={isActive('/gallery') ? 'school-primary fw-bold' : ''}
              onClick={() => setExpanded(false)}
            >
              Gallery
            </Nav.Link>
            
            <Nav.Link 
              as={Link} 
              to="/contact" 
              className={isActive('/contact') ? 'school-primary fw-bold' : ''}
              onClick={() => setExpanded(false)}
            >
              Contact
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
