import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Alert, Card } from 'react-bootstrap';
import { messagesAPI } from '../services/api';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: ''
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess(false);

    try {
      await messagesAPI.submitMessage(formData);
      setSuccess(true);
      setFormData({
        name: '',
        email: '',
        phone: '',
        subject: '',
        message: ''
      });
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to send message. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="py-5">
      <h1 className="text-center school-primary mb-5">Contact Us</h1>
      
      <Row className="g-5">
        {/* Contact Information */}
        <Col lg={4}>
          <Card className="h-100 border-0 shadow-sm">
            <Card.Body className="p-4">
              <h3 className="school-primary mb-4">Get in Touch</h3>
              
              <div className="mb-4">
                <h5 className="school-secondary">📍 Address</h5>
                <p className="text-muted">
                  123 Education Street<br />
                  Learning City, LC 12345<br />
                  United States
                </p>
              </div>
              
              <div className="mb-4">
                <h5 className="school-secondary">📞 Phone</h5>
                <p className="text-muted">
                  Main Office: +1 (555) 123-4567<br />
                  Admissions: +1 (555) 123-4568<br />
                  Fax: +1 (555) 123-4569
                </p>
              </div>
              
              <div className="mb-4">
                <h5 className="school-secondary">✉️ Email</h5>
                <p className="text-muted">
                  General: info@genesisschool.com<br />
                  Admissions: admissions@genesisschool.com<br />
                  Support: support@genesisschool.com
                </p>
              </div>
              
              <div className="mb-4">
                <h5 className="school-secondary">🕒 Office Hours</h5>
                <p className="text-muted">
                  Monday - Friday: 8:00 AM - 5:00 PM<br />
                  Saturday: 9:00 AM - 2:00 PM<br />
                  Sunday: Closed
                </p>
              </div>
            </Card.Body>
          </Card>
        </Col>
        
        {/* Contact Form */}
        <Col lg={8}>
          <Card className="border-0 shadow-sm">
            <Card.Body className="p-4">
              <h3 className="school-primary mb-4">Send us a Message</h3>
              
              {success && (
                <Alert variant="success" className="mb-4">
                  <Alert.Heading>Message Sent Successfully!</Alert.Heading>
                  <p>Thank you for contacting us. We will get back to you soon.</p>
                </Alert>
              )}
              
              {error && (
                <Alert variant="danger" className="mb-4">
                  {error}
                </Alert>
              )}
              
              <Form onSubmit={handleSubmit}>
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Full Name *</Form.Label>
                      <Form.Control
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        required
                        placeholder="Enter your full name"
                      />
                    </Form.Group>
                  </Col>
                  
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Email Address *</Form.Label>
                      <Form.Control
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                        placeholder="Enter your email"
                      />
                    </Form.Group>
                  </Col>
                </Row>
                
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Phone Number</Form.Label>
                      <Form.Control
                        type="tel"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        placeholder="Enter your phone number"
                      />
                    </Form.Group>
                  </Col>
                  
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Subject</Form.Label>
                      <Form.Control
                        type="text"
                        name="subject"
                        value={formData.subject}
                        onChange={handleChange}
                        placeholder="Enter message subject"
                      />
                    </Form.Group>
                  </Col>
                </Row>
                
                <Form.Group className="mb-4">
                  <Form.Label>Message *</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={5}
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    required
                    placeholder="Enter your message"
                  />
                </Form.Group>
                
                <Button
                  type="submit"
                  variant="primary"
                  size="lg"
                  disabled={loading}
                  className="btn-school-primary"
                >
                  {loading ? 'Sending...' : 'Send Message'}
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      
      {/* Map Section */}
      <Row className="mt-5">
        <Col>
          <Card className="border-0 shadow-sm">
            <Card.Body className="p-4">
              <h3 className="school-primary mb-4">Find Us</h3>
              <div 
                className="bg-light d-flex align-items-center justify-content-center"
                style={{ height: '300px' }}
              >
                <div className="text-center text-muted">
                  <div style={{ fontSize: '3rem' }}>🗺️</div>
                  <p>Interactive map will be available here</p>
                  <small>123 Education Street, Learning City, LC 12345</small>
                </div>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Contact;
